import React from 'react';
import '../style/Team.css'

class Team extends React.Component {

    constructor(props) {
        super(props);
        this.state = {name: props.name, correct: 0, incorrect: 0, final: 0};
    }

    notClick = () => {
        this.setState(state => ({
            correct: this.state.correct - 1
        }));
      }

    handleClick = () =>  {
        this.setState(state => ({
            correct: this.state.correct + 1
        }));
    }

    render() {
        return (<div class="block">
            <br />
            <h2><span role="img" aria-label="team">☄️ </span> Team: {this.state.name}</h2>
            <br />
            <div class="row">
                <div class="col maj">
                    <h3>{this.state.correct}</h3>
                    <h5>correct</h5>
                    <h4><a onClick={this.increaseCorrect}>⬆️</a> <a onClick={this.decreaseCorrect}>⬇️</a></h4>
                </div>
                <div class="col">
                    <h3>{this.state.incorrect}</h3>
                    <h5>incorrect</h5>
                </div>
                <div class="col">
                    <h3>{this.state.final}</h3>
                    <h5>total</h5>
                </div>
            </div>
            <br />
        </div> );
    }
}

export default Team;
