import React from 'react';
import '../style/Team.css'

class Team extends React.Component {

    constructor(props) {
        super(props);
        this.state = {name: props.name, correct: 0, incorrect: 0, final: 0};
        this.handleClick = this.handleClick.bind(this);
    }

    handleClick() {
        this.setState(state => ({
            score: this.state.score + 1
        }));
    }


    render() {
        return (<div class="block">
            <br />
            <h2><span role="img" aria-label="team">☄️ </span> Team: {this.state.name}</h2>
            <br />
            <div class="row">
                <div class="col maj" >
                    <h3>{this.state.correct}</h3>
                    <h5>correct</h5>
                    <h4 onClick={this.handleClick}>⬆️</h4>
                </div>
                <div class="col">
                    <h3>{this.state.incorrect}</h3>
                    <h5>incorrect</h5>
                </div>
                <div class="col">
                    <h3>{this.state.final}</h3>
                    <h5>total</h5>
                </div>
            </div>
            <br />
        </div> );
    }
}

